create table player
(
    n4_id           integer generated by default as identity
        primary key,
    str_name        varchar(50)  not null,
    str_full_name   varchar(50)  not null,
    str_avatar_link varchar(150) not null,
    str_nationality varchar(30)  not null,
    dt_birthday     date         not null,
    is_right_foot   boolean      not null,
    n4_kit_number   integer      not null,
    n4_height       integer      not null,
    str_role        text,
    n4_salary       integer,
    str_status      varchar(50)  not null
);

comment on column player.n4_height is 'positive';

comment on column player.n4_salary is 'positive';

alter table player
    owner to pldjango;

create index idx_player_role
    on player (str_role);

