create table team
(
    n4_id            integer generated by default as identity
        primary key,
    str_name         varchar(50),
    str_acronym_name varchar(50),
    str_logo_link    varchar(100),
    str_location     varchar(32)
);

alter table team
    owner to pldjango;

create unique index idx_team_unique
    on team (str_name);

